let greeting = "";
if (currHours >= 0 && currHours < 12) {
    greeting = "Good Morning";
    cssStyle.color = "green";
}
else if (currHours > 12 && currHours <= 15) {
    greeting = "Good Afternoon";
    cssStyle.color = "yellow";
}
else if( currHours > 15 && currHours <= 19){
    greeting = "Good Evening";
    cssStyle.color = "lightskyblue";
    }
else {
    greeting = "Good Night";
    cssStyle.color = "black";
}
ReactDOM.render(<>
    <h1>Hello Mayank,<span style={cssStyle}>{greeting}</span></h1>
